package java_exam;

public class ArithmaticOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n = 0;
		
		System.out.println(n++);
		System.out.println(n);
		System.out.println(++n);
		
		n = 0;
		System.out.println(--n);
		System.out.println(n--);
		System.out.println(n);

		
		int m = 10;
		System.out.print(++m);
		System.out.print(m++);
		System.out.println(m);
		// 11 11 12
		
		
		System.out.print(--m);
		System.out.print(m--);
		System.out.println(m);
		// 11 11 10
		
		
	
	}

}
